import axios from 'axios';

// 账号名称和密码
const username = 'your-username';
const password = 'your-password';

// 发送post请求
function sendPostRequest（response） {
  axios.post（'/api/user', {
    username,
    password
  }）
  .then（response => {
    // 处理响应
    console.log（response.data）;
  }）
  .catch（error => {
    console.log（error）;
  }）;
}

// 允许页面跳转到用户页面
function allowUserPage（） {
  const response = axios.get（'/api/user'）;
  if （response.data.success） {
    // 允许页面跳转到用户页面
    window.location.href = '/user';
  } else {
    // 不允许页面跳转到用户页面
    console.log（'User page cannot be跳转ed'）;
  }
}

// 调用允许页面跳转到用户页面的函数
allowUserPage（）;

// 发送post请求
sendPostRequest（true）;
```

