```javascript
// 引入axios库
var axios = require('axios');

// 准备请求参数
var account = 'example@example.com'; // 账号名称
var password = 'password123'; // 密码

// 发送post请求
axios.post('/login', {
  account: account,
  password: password
})
  .then(function (response) {
    // 请求成功，根据响应中的布尔值判断是否允许跳转
    var allowRedirect = response.data;

    if (allowRedirect) {
      // 允许跳转到用户页面
      window.location.href = '/user';
    } else {
      // 不允许跳转，进行其他操作
      console.log('跳转被拒绝');
    }
  })
  .catch(function (error) {
    // 请求失败，处理错误
    console.log(error);
  });
```

